import React from 'react';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Services from './Services';
import Footer from './Footer';
import '../styles/App.css';

class App extends React.Component {
  handleButtonClick = () => {
    // Add your custom logic for button click events here
    console.log('Button clicked!');
  };

  componentDidMount() {
    // Add any additional JavaScript functionality you need on component mount
    // For example, you can fetch dynamic content from an API and update the state
  }

  smoothScrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  render() {
    return (
      <Router>
        <div className="App">
          <Navbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/services" element={<Services />} />
          </Routes>
          <Footer />

          <button onClick={this.handleButtonClick}>Click me!</button>

          <div className="scroll-to-top" onClick={this.smoothScrollToTop}>
            Scroll to Top
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
