import React from 'react';
import '../styles/Footer.css';
import { Link } from 'react-router-dom';

class Footer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      phone: '',
      message: ''
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Add your form submission logic here
    // For example, you can use an API or send an email with the form data
    console.log(this.state);
    // Reset form fields
    this.setState({ name: '', email: '', phone: '', message: '' });
  };

  render() {
    return (
      <footer className="footer">
        <div className="contact-form">
          <h2>Contact Us</h2>
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={this.state.name}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={this.state.email}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="phone">Phone:</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={this.state.phone}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="message">Message:</label>
              <textarea
                id="message"
                name="message"
                value={this.state.message}
                onChange={this.handleInputChange}
                required
              ></textarea>
            </div>
            <button type="submit">Send Message</button>
          </form>
        </div>
        <div className="google-maps">
          {/* Replace the src attribute value with your Google Maps embed code */}
          <iframe
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2949.3216642999256!2d-83.64373898429293!3d42.227446979196596!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x883cae80c601b8e3%3A0x7a34bb3e14db4e4!2s4609%20Cherry%20Blossom%20Dr%2C%20Ypsilanti%2C%20MI%2048197%2C%20USA!5e0!3m2!1sen!2s!4v1626183082967!5m2!1sen!2s"
            allowFullScreen
            title="Google Maps"
          ></iframe>
        </div>
        <p className="copy-right">&copy; 2023 Techbitz. All rights reserved.</p>
      </footer>
    );
  }
}

export default Footer;
